import {StatusBar} from 'expo-status-bar';
import React, {useState} from 'react';
import {Button, StyleSheet, Text, TextInput, View} from 'react-native';

export default function App() {
    const [enteredTask, setEnteredTask] = useState("");
    const [taskList, setTaskList] = useState(Array<string>());

    const taskInputHandler = (task: string) => setEnteredTask(task)

    const addTaskHandler = () => setTaskList(taskList => [...taskList, enteredTask])

    return (
        <View style={styles.container}>
            <View style={styles.inputArea}>
                <TextInput style={styles.textInput}
                           placeholder="Write a task" value={enteredTask}
                           onChangeText={taskInputHandler}/>
                <Button title="Add" onPress={addTaskHandler}/>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        padding: 50
    },
    inputArea: {
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center",
    },
    textInput: {
        borderBottomColor: "black",
        borderBottomWidth: 1,
        padding: 10,
        width: "80%"
    }
});
