import {StatusBar} from 'expo-status-bar';
import React, {useState} from 'react';
import {Button, FlatList, ScrollView, StyleSheet, Text, TextInput, View} from 'react-native';
import TaskItem from "./components/TaskItem";
import TaskInput from "./components/TaskInput";

export default function App() {
    const [taskList, setTaskList] = useState(Array<{key: string, value: string}>())

    const addTaskHandler = (taskTitle : string) => {
        setTaskList(taskList => [...taskList, {key: `${Math.random()}`, value: taskTitle}])
    }

    const deleteTaskHandler = (taskToRemoveKey: string) => {
        setTaskList(taskList => {
            return taskList.filter(tasks => tasks.key !== taskToRemoveKey)
        })
    }

    return (
        <View style={styles.container}>
            <TaskInput onAddTask={addTaskHandler} />
            <FlatList data={taskList}
                      renderItem={task => <TaskItem taskTitle={task.item.value} onDeleteTask={() => deleteTaskHandler(task.item.key)}/>
            } />
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        padding: 40
    },
    inputArea: {
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center",
    },
    textInput: {
        borderBottomColor: "black",
        borderBottomWidth: 1,
        padding: 10,
        width: "80%"
    },
});
