import {StatusBar} from 'expo-status-bar';
import React, {useState} from 'react';
import {Button, FlatList, ScrollView, StyleSheet, Text, TextInput, View} from 'react-native';
import TaskItem from "./components/TaskItem";

export default function App() {
    const [enteredTask, setEnteredTask] = useState("")
    const [taskList, setTaskList] = useState(Array<{key: string, value: string}>())

    const taskInputHandler = (task: string) => setEnteredTask(task)

    const addTaskHandler = () => {
        setTaskList(taskList => [...taskList, {key: `${taskList.length + 1}`, value: enteredTask}])
        //setEnteredTask("")
    }

    return (
        <View style={styles.container}>
            <View style={styles.inputArea}>
                <TextInput style={styles.textInput}
                           placeholder="Write a task" value={enteredTask}
                           onChangeText={taskInputHandler}/>
                <Button title="Add" onPress={addTaskHandler}/>
            </View>
            <FlatList data={taskList}
                      renderItem={task => <TaskItem taskTitle={task.item.value} />
            } />
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        padding: 40
    },
    inputArea: {
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center",
    },
    textInput: {
        borderBottomColor: "black",
        borderBottomWidth: 1,
        padding: 10,
        width: "80%"
    },
});
